import React from "react";
import styled from "styled-components";
import { FaEdit, FaTrash } from "react-icons/fa";

const Card = styled.div`
  width: 200px;
  background-color: #fff;
  padding: 20px;
  box-shadow: 0px 0px 5px #ccc;
  border-radius: 5px;
  margin: 10px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const IconContainer = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 10px;
`;

const Grid = ({ produtos, setProdutos, setOnEdit }) => {

  const handleEdit = (item) => {
    setOnEdit(item);
  };

  const handleDelete = async (id) => {
    await axios
      .delete("http://localhost:8800/" + id)
      .then(({ data }) => {
        const newArray = produtos.filter((produto) => produto.id !== id);

        setProdutos(newArray);
        toast.success(data);
      })
      .catch(({ data }) => toast.error(data));

    setOnEdit(null);
  };


  return (
    <div style={{ display: "flex", flexWrap: "wrap" }}>
      {produtos.map((item, i) => (
        <Card key={i}>
            <img src="produtos.jpeg" alt="" width="100%" marginBottom="10px"></img>
          <h3>{item.nome_produto}</h3>
          <p>Código: {item.cod_produto}</p>
          <p>Descrição: {item.desc_produto}</p>
          <p>Preço: {Number(item.preco_produto).toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}</p>
          <IconContainer>
            <FaEdit onClick={()=>handleEdit(item)}/>
            <FaTrash onClick={()=>handleDelete(item.id)}/>
          </IconContainer>
        </Card>
      ))}
    </div>
  );
};

export default Grid;
