import React from "react";
//import axios from "axios";
import styled from "styled-components";
import {FaTrash, FaEdit} from "react-icons/fa";
//import { FaDelete } from 'react-icons/fa';
//import {toast} from "react-toastify";

const Table = styled.table`
    width: 100%;
    background-color: #fff;
    padding: 20px;
    box-shodow: 0px 0px 5px #ccc;
    border-radius: 5px;
    max-width: 800px;
    margin: 20px auto;
    word-break: break-all;
`;

export const Thead = styled.thead``;

export const Tr = styled.tr``;

export const Tbody = styled.tbody``;

const Grid = ({produtos}) => {
    return (
        <Table>
            <Thead>
                LISTA DE PRODUTOS
            </Thead>
            <Tbody>
                {produtos.map((item,i) => (
                    <dl key={i}>
                        <dt width="30%">{item.nome_produto}</dt>
                        <dt width="30%">{item.cod_produto}</dt>
                        <dt width="30%">{item.desc_produto}</dt>
                        <dt width="30%">{item.preco_produto}</dt>
                        <dt alienCenter width="5%"><FaEdit/></dt>
                    </dl>
                ))};
            </Tbody>
        </Table>
    );
};

export default Grid;
