import React from "react";
import styled from "styled-components";
import { FaEdit } from "react-icons/fa";

const Card = styled.div`
  width: 200px;
  background-color: #fff;
  padding: 20px;
  box-shadow: 0px 0px 5px #ccc;
  border-radius: 5px;
  margin: 10px;
`;

const Grid = ({ produtos }) => {
    const imagemPadrao = './public/produtos.jpeg'
  return (
    <div style={{ display: "flex", flexWrap: "wrap" }}>
      {produtos.map((item, i) => (
        <Card key={i}>
            <img
            src={item.imagem_produto || imagemPadrao}
            alt={item.nome_produto}
            style={{ width: '100%', marginBottom: '10px' }}
          />
          <h3>{item.nome_produto}</h3>
          <p>Código: {item.cod_produto}</p>
          <p>Descrição: {item.desc_produto}</p>
          <p>Preço: {Number(item.preco_produto).toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}</p>
          <div style={{ textAlign: "center" }}>
            <FaEdit />
          </div>
        </Card>
      ))}
    </div>
  );
};

export default Grid;
