import React from "react";
import axios from "axios";
import styled from "styled-components";
import { FaEdit } from "react-icons/fa";
import { toast } from "react-toastify";

const Card = styled.div`
  width: 100%;
  background-color: #fff;
  padding: 20px;
  box-shadow: 0px 0px 5px #ccc;
  border-radius: 5px;
  max-width: 800px;
  margin: 20px auto;
  word-break: break-all;
`;

const CardHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 10px;
`;

const CardTitle = styled.h3`
  margin: 0;
`;

const CardBody = styled.div``;

const CardAction = styled.div`
  text-align: center;
  margin-top: 10px;
`;

const Grid = ({ produtos }) => {
  return (
    <>
      {produtos.map((item, i) => (
        <Card key={i}>
          <CardHeader>
            <CardTitle>{item.nome_produto}</CardTitle>
            <FaEdit />
          </CardHeader>
          <CardBody>
            <p><strong>Código:</strong> {item.cod_produto}</p>
            <p><strong>Descrição:</strong> {item.desc_produto}</p>
            <p><strong>Preço:</strong> {item.preco_produto}</p>
          </CardBody>
          <CardAction>
            {/* Additional actions can be added here */}
          </CardAction>
        </Card>
      ))}
    </>
  );
};

export default Grid;
