//import axios from "axios";
import React, { useEffect, useRef } from "react";
import styled from "styled-components";
//import { toast } from "react-toastify";

const FormContainer = styled.form`
    display: flex;
    align-items: flex-end;
    gap: 10px;
    flex-wrap: wrap;
    background-color:#fff;
    padding: 20px;
    box-shadow: 0px 0px 5px #ccc;
    border-radius: 5px;
`;

const InputArea = styled.div`
    display: flex;
    flex-direction: column;
`;

const Label = styled.label``;

const Input = styled.input`
    width: 120px;
    padding: 0px 10px;
    border: 1px solid #bbb;
    border-radius: 5px;
    height: 40px;
`;

const Button = styled.button`
    padding: 10px;
    height: 42px;
`;


const Form = (onEdit) => {
    const ref = useRef();

    useEffect(() => {
        if (onEdit) {
          const produto = ref.current;
    
          produto.nome_produto.value = onEdit.nome_produto;
          produto.cod_produto.value = onEdit.cod_produto;
          produto.desc_produto.value = onEdit.desc_produto;
          produto.preco_produto.value = onEdit.preco_produto;
        }
      }, [onEdit]);

    return (
        <FormContainer ref={ref}>
            <InputArea>
            <Label>Nome</Label>
            <Input name="nome_produto"/>
            </InputArea>
            <InputArea>
            <Label>Código</Label>
            <Input name="cod_produto"/>
            </InputArea>
            <InputArea>
            <Label>Descrição</Label>
            <Input name="desc_produto"/>
            </InputArea>
            <InputArea>
            <Label>Preço</Label>
            <Input name="preco_produto"/>
            </InputArea>

            <Button type="submit">SALVAR</Button>
        </FormContainer>
    );
};

export default Form;
